-- Create entries from sales (inputs)
SELECT 
  'input' as direction,
  'sale' as entry_type,
  COALESCE(description, CONCAT('Sale - ', type)) as name,
  amount,
  description,
  date as entry_date,
  id as reference_id,
  true as is_active,
  created_at,
  updated_at
FROM sales;

-- Create entries from expenses (outputs)
SELECT 
  'output' as direction,
  'expense' as entry_type,
  name,
  amount,
  description,
  category,
  vendor,
  COALESCE(expense_date, start_date) as entry_date,
  id as reference_id,
  is_active,
  created_at,
  updated_at
FROM expenses;

-- Create OUTPUT entries from expense projection entries (individual payment dates)
SELECT 
  'output' as direction,
  'expense_payment' as entry_type,
  CONCAT(e.name, ' - ', epe.month) as name,
  COALESCE(epe.actual_amount, epe.amount) as amount,
  epe.notes as description,
  CONCAT(epe.month, '-01')::DATE as entry_date,
  CONCAT(epe.month, '-01')::DATE as due_date,
  epe.expense_id as reference_id,
  epe.id as schedule_entry_id,
  true as is_active,
  epe.created_at,
  epe.updated_at
FROM expense_projection_entries epe
INNER JOIN expenses e ON e.id = epe.expense_id;

